DataSource.Query:
WITH invoice_purchases AS ( SELECT purchases.supplier_id, SUM(checkout_detail_view.total) AS amount FROM purchase.purchases LEFT JOIN inventory.checkout_detail_view ON checkout_detail_view.checkout_id = purchases.checkout_id LEFT JOIN finance.transaction_master ON transaction_master.transaction_master_id = checkout_detail_view.transaction_master_id WHERE transaction_master.verification_status_id > 0 AND transaction_master.deleted = 0 AND transaction_master.office_id IN (SELECT office_id FROM core.get_office_ids(@OfficeId)) AND transaction_master.value_date BETWEEN @From AND @To GROUP BY supplier_id ), bank AS ( SELECT supplier_payments.supplier_id, SUM(supplier_payments.amount) AS amount FROM purchase.supplier_payments LEFT JOIN finance.transaction_master ON transaction_master.transaction_master_id = supplier_payments.transaction_master_id WHERE COALESCE(supplier_payments.cash_repository_id, 0) = 0 AND transaction_master.verification_status_id > 0 AND transaction_master.deleted = 0 AND transaction_master.office_id IN (SELECT office_id FROM core.get_office_ids(@OfficeId)) AND transaction_master.value_date BETWEEN @From AND @To GROUP BY supplier_id ), cash AS ( SELECT supplier_payments.supplier_id, SUM(supplier_payments.amount) AS amount FROM purchase.supplier_payments LEFT JOIN finance.transaction_master ON transaction_master.transaction_master_id = supplier_payments.transaction_master_id WHERE COALESCE(supplier_payments.cash_repository_id, 0) > 0 AND COALESCE(supplier_payments.check_amount, 0) = 0 AND transaction_master.verification_status_id > 0 AND transaction_master.deleted = 0 AND transaction_master.office_id IN (SELECT office_id FROM core.get_office_ids(@OfficeId)) AND transaction_master.value_date BETWEEN @From AND @To GROUP BY supplier_payments.supplier_id ), all_data AS ( SELECT supplier_id, amount AS invoice_purchases, null AS bank, null AS cash FROM invoice_purchases UNION ALL
SELECT supplier_id, null AS total_payment, amount AS bank, null AS cash FROM bank UNION ALL
SELECT supplier_id, null AS total_payment, null AS bank, amount AS cash FROM cash ) SELECT suppliers.supplier_code, suppliers.supplier_name, SUM(COALESCE(invoice_purchases, 0)) AS invoice_purchases, SUM(COALESCE(cash, 0)) AS cash, SUM(COALESCE(bank, 0)) AS bank, SUM(COALESCE(cash, 0)) + sum(COALESCE(bank, 0)) AS total_sales, SUM(COALESCE(invoice_purchases, 0)) - (SUM(COALESCE(cash, 0)) + sum(COALESCE(bank, 0))) AS balance FROM all_data LEFT JOIN inventory.suppliers ON suppliers.supplier_id = all_data.supplier_id WHERE suppliers.deleted = 0 AND COALESCE(@SupplierId, 0) = 0 OR all_data.supplier_id = @SupplierId GROUP BY suppliers.supplier_code, suppliers.supplier_name;
