DataSource.Query:
SELECT finance.transaction_master.transaction_master_id, finance.transaction_master.transaction_ts, finance.transaction_master.value_date, account.users.name AS entered_by, core.verification_statuses.verification_status_name AS status, verified_by_user.name AS verified_by, finance.transaction_master.verification_reason, finance.transaction_master.transaction_code, core.offices.office_name, finance.cost_centers.cost_center_name, finance.transaction_master.reference_number, finance.transaction_master.statement_reference, inventory.suppliers.supplier_name, inventory.shippers.company_name AS shipper_name, SUM ( CASE WHEN inventory.get_return_type(inventory.checkouts.checkout_id) = 'Dr' THEN 1 ELSE -1 END * inventory.checkouts.taxable_total ) AS taxable_total, SUM ( CASE WHEN inventory.get_return_type(inventory.checkouts.checkout_id) = 'Dr' THEN 1 ELSE -1 END * inventory.checkouts.discount ) AS discount, AVG ( inventory.checkouts.tax_rate ) AS tax_rate, SUM ( CASE WHEN inventory.get_return_type(inventory.checkouts.checkout_id) = 'Dr' THEN 1 ELSE -1 END * inventory.checkouts.tax ) AS tax, SUM ( CASE WHEN inventory.get_return_type(inventory.checkouts.checkout_id) = 'Dr' THEN 1 ELSE -1 END *
(COALESCE(inventory.checkouts.taxable_total, 0) + COALESCE(inventory.checkouts.tax, 0) - COALESCE(inventory.checkouts.discount, 0)) ) AS sub_total,
SUM ( CASE WHEN inventory.get_return_type(inventory.checkouts.checkout_id) = 'Dr' THEN 1 ELSE -1 END * inventory.checkouts.nontaxable_total ) AS nontaxable_total, SUM ( CASE WHEN inventory.get_return_type(inventory.checkouts.checkout_id) = 'Dr' THEN 1 ELSE -1 END * (COALESCE(inventory.checkouts.taxable_total, 0) + COALESCE(inventory.checkouts.tax, 0) + COALESCE(inventory.checkouts.nontaxable_total, 0) - COALESCE(inventory.checkouts.discount, 0)) ) AS total FROM finance.transaction_master LEFT JOIN core.offices ON finance.transaction_master.office_id = core.offices.office_id LEFT JOIN finance.cost_centers ON finance.transaction_master.cost_center_id = finance.cost_centers.cost_center_id LEFT JOIN account.users ON finance.transaction_master.user_id = account.users.user_id LEFT JOIN inventory.checkouts ON finance.transaction_master.transaction_master_id = inventory.checkouts.transaction_master_id LEFT JOIN purchase.purchase_returns ON purchase.purchase_returns.checkout_id = inventory.checkouts.checkout_id LEFT JOIN inventory.suppliers ON purchase.purchase_returns.supplier_id = inventory.suppliers.supplier_id LEFT JOIN core.verification_statuses ON finance.transaction_master.verification_status_id = core.verification_statuses.verification_status_id LEFT JOIN account.users AS verified_by_user ON finance.transaction_master.verified_by_user_id = verified_by_user.user_id LEFT JOIN inventory.shippers ON inventory.checkouts.shipper_id = inventory.shippers.shipper_id WHERE finance.transaction_master.book = 'Purchase Return' AND finance.transaction_master.transaction_master_id = @transaction_master_id GROUP BY finance.transaction_master.transaction_master_id, finance.transaction_master.transaction_ts, finance.transaction_master.value_date, account.users.name, core.verification_statuses.verification_status_name, verified_by_user.name, finance.transaction_master.verification_reason, finance.transaction_master.transaction_code, core.offices.office_name, finance.cost_centers.cost_center_name, finance.transaction_master.reference_number, finance.transaction_master.statement_reference, inventory.suppliers.supplier_name, inventory.shippers.company_name;
